DROP TABLE IF EXISTS `users`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `users` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `username` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '账号',
  `email` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '邮箱',
  `phone` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '手机号',
  `password_hash` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '密码哈希',
  `nickname` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '昵称',
  `avatar` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '头像',
  `background_image` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '??????',
  `posts_visibility` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT 'public' COMMENT '帖子可见性',
  `likes_visibility` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT 'public' COMMENT '获赞可见性',
  `following_visibility` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT 'public' COMMENT '关注可见性',
  `followers_visibility` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT 'public' COMMENT '粉丝可见性',
  `gender` enum('male','female','secret') COLLATE utf8mb4_unicode_ci DEFAULT 'secret' COMMENT '性别',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `location` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '地区',
  `bio` text COLLATE utf8mb4_unicode_ci COMMENT '个性签名',
  `mentions` json DEFAULT NULL,
  `interest_tags` json DEFAULT NULL COMMENT '兴趣标签',
  `points` int DEFAULT '0' COMMENT '积分',
  `status` enum('active','frozen','deleted') COLLATE utf8mb4_unicode_ci DEFAULT 'active' COMMENT '状态',
  `last_login_at` timestamp NULL DEFAULT NULL COMMENT '最后登录时间',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `privacy_settings` json DEFAULT NULL,
  `notification_settings` json DEFAULT NULL,
  `online_status` enum('online','offline','away','busy') COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'online',
  `default_landing_page` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT 'timeline' COMMENT '默认落地页',
  `is_guest` tinyint(1) DEFAULT '0' COMMENT '是否为游客',
  `is_visible` tinyint(1) DEFAULT '1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  UNIQUE KEY `phone` (`phone`),
  UNIQUE KEY `email` (`email`),
  KEY `idx_username` (`username`),
  KEY `idx_email` (`email`),
  KEY `idx_phone` (`phone`),
  KEY `idx_status` (`status`),
  KEY `idx_created_at` (`created_at`),
  KEY `idx_status_visible` (`status`,`is_visible`),
  KEY `idx_points` (`points`)
) ENGINE=InnoDB AUTO_INCREMENT=1000435 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='用户表';
